package javassignment;
//Q4 println
public class printnos {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n1=1;
		int n2=2;
		int n3=3;
		int n4=4;
		System.out.println("Using System.out.println statements");
		System.out.println(+n1+" "+n2+" "+n3+" "+n4+" ");
		System.out.println("-------------------------------------------------");
		System.out.println("Using System.out.print statements");
		System.out.print(n1+" ");
		System.out.print(n2+" ");
		System.out.print(n3+" ");
		System.out.print(n4+"\n ");
		System.out.print("-------------------------------------------------");
		System.out.println("Using System.out.printf statements");
		System.out.printf("%d %d %d %d" ,n1,n2,n3,n4);
		
		

	}

}
